            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.ok) {
        const data = await response.json();
        setStats(data);
      } else {
        // Fallback data if backend not available
        setStats({
          totalProducts: 25,
          totalOrders: 150,
          totalRevenue: 45000000,
