import type React from "react"
import { Link, useLocation, useNavigate } from "react-router-dom"
import { useAuth } from "../contexts/AuthContext"

interface LayoutProps {
  children: React.ReactNode
}

const Layout: React.FC<LayoutProps> = ({ children }) => {
  const location = useLocation()
  const navigate = useNavigate()
  const { logout } = useAuth()

  const handleLogout = () => {
    logout()
    navigate("/login")
  }

  const menuItems = [
    { path: "/", label: "Dashboard", icon: "üìä" },
    { path: "/products", label: "S·∫£n ph·∫©m", icon: "üì¶" },
    { path: "/orders", label: "ƒê∆°n h√†ng", icon: "üõí" },
    { path: "/inventory", label: "Nh·∫≠p/Xu·∫•t kho", icon: "üìã" },
    { path: "/reports", label: "B√°o c√°o", icon: "üìà" },
  ]

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center">
              <h1 className="text-xl font-semibold text-gray-900">Initation Vi·ªát Nam - Qu·∫£n l√Ω kho h√†ng</h1>
            </div>
            <button onClick={handleLogout} className="btn-danger">
              ƒêƒÉng xu·∫•t
            </button>
          </div>
        </div>
      </header>

      <div className="flex">
        {/* Sidebar */}
        <nav className="w-64 bg-white shadow-sm min-h-screen">
          <div className="p-4">
            <ul className="space-y-2">
              {menuItems.map((item) => (
                <li key={item.path}>
                  <Link
                    to={item.path}
                    className={`flex items-center space-x-3 text-gray-700 p-2 rounded-lg hover:bg-gray-100 ${
                      location.pathname === item.path ? "bg-blue-100 text-blue-700" : ""
                    }`}
                  >
                    <span className="text-lg">{item.icon}</span>
                    <span>{item.label}</span>
                  </Link>
                </li>
              ))}
            </ul>
          </div>
        </nav>

        {/* Main Content */}
        <main className="flex-1 p-6">{children}</main>
      </div>
    </div>
  )
}

export default Layout
