"use client"

import type React from "react"
import { createContext, useContext, useState, useEffect } from "react"

interface AuthContextType {
  isAuthenticated: boolean
  user: any
  login: (username: string, password: string) => Promise<boolean>
  logout: () => void
}

const AuthContext = createContext<AuthContextType | undefined>(undefined)

export function useAuth() {
  const context = useContext(AuthContext)
  if (context === undefined) {
    throw new Error("useAuth must be used within an AuthProvider")
  }
  return context
}

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [isAuthenticated, setIsAuthenticated] = useState(false)
  const [user, setUser] = useState(null)

  useEffect(() => {
    const checkAuthStatus = async () => {
      const token = localStorage.getItem("token")
      if (token) {
        try {
          // Kiểm tra token có hợp lệ không
          const payload = JSON.parse(atob(token.split(".")[1]))
          const currentTime = Date.now() / 1000

          if (payload.exp > currentTime) {
            // Token còn hạn, set authenticated ngay lập tức
            setIsAuthenticated(true)
            setUser({ username: payload.username, id: payload.userId })

            // Sau đó verify với server (optional)
            try {
              const response = await fetch("http://localhost:5000/api/auth/verify", {
                headers: {
                  Authorization: `Bearer ${token}`,
                },
              })

              if (response.ok) {
                const userData = await response.json()
                setUser(userData.user)
              }
            } catch (error) {
              // Nếu server không phản hồi, vẫn giữ trạng thái đăng nhập
              console.log("Server verification failed, but token is valid")
            }
          } else {
            // Token hết hạn
            localStorage.removeItem("token")
            setIsAuthenticated(false)
            setUser(null)
          }
        } catch (error) {
          // Token không hợp lệ
          localStorage.removeItem("token")
          setIsAuthenticated(false)
          setUser(null)
        }
      }
    }

    checkAuthStatus()
  }, [])

  const login = async (username: string, password: string): Promise<boolean> => {
    try {
      const response = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      })

      if (response.ok) {
        const data = await response.json()
        localStorage.setItem("token", data.token)
        setIsAuthenticated(true)
        setUser(data.user)
        return true
      }
      return false
    } catch (error) {
      console.error("Login error:", error)
      return false
    }
  }

  const logout = () => {
    localStorage.removeItem("token")
    setIsAuthenticated(false)
    setUser(null)
  }

  const value = {
    isAuthenticated,
    user,
    login,
    logout,
  }

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>
}
